data.template:
{
    "attribute": {
        "productId": "0",
        "name": "App强样式"
    },
    "references": [
        {
            "referVarType": "main",
            "expressions": [
                {
                    "key": "title",
                    "alias": "标题",
                    "validation": {
                        "minLen": 16,
                        "maxLen": 32,
                        "required": true,
                        "illegalChars": ""
                    },
                    "tips": "标题长度16到32个字符",
                    "inputType": "text",
                    "varType": "title",
                    "wildcard": "[{\"key\": \"normal\",\"value\": \"{@}\"},{\"key\":\"region\",\"value\":\"{#region#}\"}]",
					"isShowKey":true
                },
                {
                    "key": "appname_h1",
                    "alias": "App名称",
                    "validation": {
                        "minLen": 1,
                        "maxLen": 24,
                        "required": true
                    },
                    "tips": "长度1到24个字符",
                    "inputType": "text",
                    "varType": "h1",
                    "secondaryVarType": "appname",
					"isShowKey":false
                },
                {
                    "key": "appdesc_text",
                    "alias": "App描述",
                    "validation": {
                        "minLen": 16,
                        "maxLen": 50,
                        "required": true
                    },
                    "tips": "长度16到50个字符",
                    "inputType": "text",
                    "varType": "h1",
					"isShowKey":false
                },
                {
                    "key": "show_url",
                    "alias": "显示网址",
                    "validation": {
                        "minLen": 1,
                        "maxLen": 36,
                        "required": true,
                        "illegalChars": "",
                        "type": "domainCheck"
                    },
                    "tips": "请填写显示网址，帐号注册域名或子域名",
                    "inputType": "text",
                    "varType": "url",
                    "secondaryVarType": "show",
					"isShowKey":true
                },
                {
                    "key": "target_url",
                    "alias": "目标网址",
                    "validation": {
                        "minLen": 1,
                        "maxLen": 1024,
                        "required": true,
                        "illegalChars": "",
                        "type": "domainCheck"
                    },
                    "tips": "http(s)开头，帐号注册域名或子域名",
                    "inputType": "text",
                    "varType": "url",
                    "secondaryVarType": "target",
					"isShowKey":false
                }
            ]
        },
        {
            "referVarType": "app",
            "index": 1,
            "expressions": [
                {
                    "key": "pic_img",
                    "alias": "图片",
                    "validation": {
                        "suffixes": [
                            "jpg",
                            "jpeg",
                            "png"
                        ],
                        "maxSize": 100,
                        "width": 120,
                        "height": 120,
                        "required": true
                    },
                    "tips": "请上传120*120,大小不超过100kb图片,支持jpg和png",
                    "inputType": "image",
                    "varType": "image",
					"isShowKey":false
                },
                {
                    "key": "androiddownload_url",
                    "alias": "app安卓下载地址",
                    "validation": {
                        "minLen": 0,
                        "maxLen": 1024,
                        "required": false,
                        "illegalChars": "",
                        "type": "url"
                    },
                    "tips": "请填写app下载地址，http(s)开头",
                    "inputType": "text",
                    "varType": "url",
                    "secondaryVarType": "androiddownload",
					"isShowKey":false
                },
                {
                    "key": "iosdownload_url",
                    "alias": "app ios下载地址",
                    "validation": {
                        "minLen": 0,
                        "maxLen": 1024,
                        "required": false,
                        "illegalChars": "",
                        "type": "url"
                    },
                    "tips": "请填写app下载地址，http(s)开头",
                    "inputType": "text",
                    "varType": "url",
                    "secondaryVarType": "iosdownload",
					"isShowKey":false
                }
            ]
        }
    ],
    "rules": {
        "groupRelation": [
            {
                "relationKey": "groupRequired",
                "value": [
                    {
                        "mode": "OR",
                        "groups": [
                            {
                                "mode": "AND",
                                "value": [
                                    "androiddownload_url"
                                ]
                            },
                            {
                                "mode": "AND",
                                "value": [
                                    "iosdownload_url"
                                ]
                            }
                        ],
                        "tips": "安卓下载链接和ios下载链接至少填写一个"
                    }
                ]
            }
        ]
    }
}
